function show_file_option(is_button) {
    // try
    {
        if (is_button) {
            document.getElementById("btn_select_file").style.display = '';
            document.getElementById("txt_url").style.display = 'none';
        }
        else {
            document.getElementById("btn_select_file").style.display = 'none';
            document.getElementById("txt_url").style.display = '';
        }
        document.getElementById("txt_url").value = "";
        document.getElementById("File1").value = "";
    }
}
